import React, { useState } from 'react';
import {
  Container,
  Box,
  Typography,
  Button,
  Paper,
  CircularProgress,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  Alert,
  Grid,
  Card,
  CardContent,
  Rating,
  Chip,
} from '@mui/material';
import { styled } from '@mui/material/styles';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import ErrorIcon from '@mui/icons-material/Error';
import LightbulbIcon from '@mui/icons-material/Lightbulb';
import axios from 'axios';

const VisuallyHiddenInput = styled('input')`
  clip: rect(0 0 0 0);
  clip-path: inset(50%);
  height: 1px;
  overflow: hidden;
  position: absolute;
  bottom: 0;
  left: 0;
  white-space: nowrap;
  width: 1px;
`;

const UploadBox = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  textAlign: 'center',
  cursor: 'pointer',
  border: `2px dashed ${theme.palette.primary.main}`,
  backgroundColor: theme.palette.background.default,
  '&:hover': {
    backgroundColor: theme.palette.action.hover,
  },
}));

function App() {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setResult(null);
      setError(null);
    }
  };

  const handleSubmit = async () => {
    if (!file) {
      setError('Vui lòng chọn file CV');
      return;
    }

    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:8000/evaluate-cv', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setResult(response.data);
    } catch (err) {
      setError(err.response?.data?.detail || 'Có lỗi xảy ra khi đánh giá CV');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="lg">
      <Box sx={{ my: 4 }}>
        <Typography variant="h3" component="h1" gutterBottom align="center" color="primary">
          CV Evaluator
        </Typography>
        <Typography variant="h6" align="center" color="text.secondary" paragraph>
          Đánh giá CV tự động - Cải thiện cơ hội việc làm của bạn
        </Typography>
        
        <Grid container spacing={3}>
          <Grid item xs={12} md={6}>
            <UploadBox elevation={3}>
              <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: 2 }}>
                <CloudUploadIcon sx={{ fontSize: 60, color: 'primary.main' }} />
                <Typography variant="h6">
                  {file ? 'File đã chọn: ' + file.name : 'Kéo thả hoặc click để chọn file CV'}
                </Typography>
                <Button
                  component="label"
                  variant="contained"
                  startIcon={<CloudUploadIcon />}
                >
                  Chọn File
                  <VisuallyHiddenInput type="file" onChange={handleFileChange} accept=".txt,.doc,.docx,.pdf" />
                </Button>
                <Typography variant="caption" color="text.secondary">
                  Hỗ trợ định dạng: PDF, DOC, DOCX, TXT
                </Typography>
              </Box>
            </UploadBox>
          </Grid>

          <Grid item xs={12} md={6}>
            <Card elevation={3}>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  Hướng dẫn
                </Typography>
                <List>
                  <ListItem>
                    <ListItemIcon><CheckCircleIcon color="primary" /></ListItemIcon>
                    <ListItemText primary="Tải lên file CV của bạn" />
                  </ListItem>
                  <ListItem>
                    <ListItemIcon><CheckCircleIcon color="primary" /></ListItemIcon>
                    <ListItemText primary="Hệ thống sẽ tự động phân tích và đánh giá" />
                  </ListItem>
                  <ListItem>
                    <ListItemIcon><CheckCircleIcon color="primary" /></ListItemIcon>
                    <ListItemText primary="Nhận kết quả đánh giá chi tiết và gợi ý cải thiện" />
                  </ListItem>
                </List>
              </CardContent>
            </Card>
          </Grid>
        </Grid>

        <Box sx={{ mt: 3, textAlign: 'center' }}>
          <Button
            variant="contained"
            color="primary"
            size="large"
            onClick={handleSubmit}
            disabled={!file || loading}
            sx={{ minWidth: 200 }}
          >
            {loading ? <CircularProgress size={24} /> : 'Đánh Giá CV'}
          </Button>
        </Box>

        {error && (
          <Alert severity="error" sx={{ mt: 3 }}>
            {error}
          </Alert>
        )}

        {result && (
          <Paper elevation={3} sx={{ p: 3, mt: 3 }}>
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 2 }}>
                  <Typography variant="h5">
                    Kết Quả Đánh Giá
                  </Typography>
                  <Rating value={result.score / 2} precision={0.5} readOnly />
                  <Typography variant="h6" color="primary">
                    {result.score}/10
                  </Typography>
                </Box>
                <Typography variant="body1" paragraph>
                  {result.feedback}
                </Typography>
              </Grid>

              {result.strengths.length > 0 && (
                <Grid item xs={12} md={6}>
                  <Card sx={{ bgcolor: 'success.light' }}>
                    <CardContent>
                      <Typography variant="h6" gutterBottom>
                        Điểm Mạnh
                      </Typography>
                      <List>
                        {result.strengths.map((strength, index) => (
                          <ListItem key={index}>
                            <ListItemIcon><CheckCircleIcon color="success" /></ListItemIcon>
                            <ListItemText primary={strength} />
                          </ListItem>
                        ))}
                      </List>
                    </CardContent>
                  </Card>
                </Grid>
              )}

              {result.errors.length > 0 && (
                <Grid item xs={12} md={6}>
                  <Card sx={{ bgcolor: 'error.light' }}>
                    <CardContent>
                      <Typography variant="h6" gutterBottom>
                        Cần Cải Thiện
                      </Typography>
                      <List>
                        {result.errors.map((error, index) => (
                          <ListItem key={index}>
                            <ListItemIcon><ErrorIcon color="error" /></ListItemIcon>
                            <ListItemText primary={error} />
                          </ListItem>
                        ))}
                      </List>
                    </CardContent>
                  </Card>
                </Grid>
              )}

              {result.suggestions.length > 0 && (
                <Grid item xs={12}>
                  <Card sx={{ bgcolor: 'info.light', mt: 2 }}>
                    <CardContent>
                      <Typography variant="h6" gutterBottom>
                        Gợi Ý Cải Thiện
                      </Typography>
                      <List>
                        {result.suggestions.map((suggestion, index) => (
                          <ListItem key={index}>
                            <ListItemIcon><LightbulbIcon color="info" /></ListItemIcon>
                            <ListItemText primary={suggestion} />
                          </ListItem>
                        ))}
                      </List>
                    </CardContent>
                  </Card>
                </Grid>
              )}
            </Grid>
          </Paper>
        )}
      </Box>
    </Container>
  );
}

export default App;
